$(document).ready(function() {
    var b=$(".carousel .item");
    var a=$(window).height();
    b.eq(0).addClass("active");
    b.height(a);
    b.addClass("full-screen");
    $(".carousel img").each(function() {
        var c=$(this).attr("src");
        var d=$(this).attr("data-color");
        $(this).parent().css( {
            "background-image": "url("+c+")", "background-color": d
        }
        );
        $(this).remove()
    }
    );
    $(window).on("resize",
    function() {
        a=$(window).height();
        b.height(a)
    }
    );
    $(".carousel").carousel( {
        interval: 10000, pause: "false"
    }
    );
    $(".animated").css("animation-duration",
    "4s");
    $("#submit").click(function() {
        $.post("contactus.php", $(".frm").serialize(), function(c) {
            $("#success").html(c)
        }
        );
        return false
    }
    );
    $("body").scrollspy( {
        target: "#navbar"
    }
    );
    $('a[href^="#"]').on("click",
    function(f) {
        f.preventDefault();
        var d=this.hash;
        var c=$(d);
        $("html, body").stop().animate( {
            scrollTop: c.offset().top
        }
        ,
        900,
        "swing");
        $(".navbar-collapse.collapse").removeClass("in")
    }
    );
    /*$(window).scroll(function() {
        if($(window).scrollTop()>0) {
            $(".site-navbar").addClass("sticky")
        }
        else {
            $(".site-navbar").removeClass("sticky")
        }
    }
    );*/
    $(".carousel").carousel( {
        interval: 5000
    }
    );
    window.sr=ScrollReveal();
    sr.reveal("#about h3, #about .col-md-6",
    {
        duration: 1500, reset: true
    }
    ,
    50);
    sr.reveal("#services h3, #services .col-md-4",
    {
        duration: 1500, reset: true
    }
    ,
    50);
    sr.reveal("#portfolio h3, #portfolio .box",
    {
        duration: 1500, reset: true
    }
    ,
    50);
    sr.reveal("#pricing h3, #pricing .col-md-3",
    {
        duration: 1500, reset: true
    }
    ,
    50);
    sr.reveal("#team .col-md-12, #team .col-md-3",
    {
        duration: 1500, reset: true
    }
    ,
    50)
}
);